import React, { Component } from "react";
import axios from "axios";
import { Header, Form, Input } from "semantic-ui-react";
import { Button, ButtonGroup, Row, Container, Col} from 'react-bootstrap';

let endpoint = "http://localhost:3001";

class ShakeSite extends Component {
  constructor(props) {
    super(props);
    // this.onSubmit = this.onSubmit.bind(this);
    this.onChangeSearchString = this.onChangeSearchString.bind(this);

    this.state = {
      searchString: '',
      searchResults: ["hammie"],
      selectedSearchResult: ''
    };
  }

  componentDidUpdate(prevProps, prevState) {
      if (prevState.searchString !== this.state.searchString) {

      }
  }

  onChangeSearchString(e) {
      this.setState({searchString: e.target.value})
  }

  onSubmit = (e) => {
      e.preventDefault();
      axios.post(endpoint + "/search", null, 
      {
          params: {q: this.state.searchString}
      }).then((response) => { 
          console.log(response);
          this.setState({searchResults: response.data});
        })
        // .catch(error => {console.log(error.response)})
    };

    displayList = () =>
        this.state.searchResults.map(result => <Row>
                                                    <Button 
                                                        className='m-1' 
                                                        variant="light" 
                                                        size="sm" 
                                                        // onClick={this.searchResultOnClick(result)}
                                                    >
                                                        {result}
                                                    </Button>
                                                </Row>)

    // searchResultOnClick = () =>


  render() {
    return (
        <Container fluid>
            <Row className="show-grid">
                <Col>
                    <Row>
                        <Col>Shakesearch</Col>
                    </Row>
                    <Row>
                        <Container fluid>
                            <Form onSubmit={this.onSubmit}>
                                <Form.Field>
                                    <Input
                                        name='searchInput'
                                        value={this.state.searchString}
                                        type="text"
                                        onChange={this.onChangeSearchString}
                                        placeholder="What art thee looking f'r"
                                    />
                                </Form.Field>
                            </Form>
                        </Container>
                    </Row>
                    <Col className="align-items-center">
                            {this.displayList()}
                    </Col>
                </Col>

                <Col>
                FUCK
                </Col>
            </Row>

        </Container>
    );
  }
}

export default ShakeSite;
