import React, { Component } from "react";
import axios from "axios";
import { Button, Header, Form, Input } from "semantic-ui-react";

let endpoint = "http://localhost:3001";

class ShakeSite extends Component {
  constructor(props) {
    super(props);
    this.onSubmit = this.onSubmit.bind(this);
    this.onChangeSearchString = this.onChangeSearchString.bind(this);

    this.state = {
      searchString: '',
      searchResults: ["hammie", "fuckyouhamlet"]
    };
  }

//   componentDidMount() {
//     this.getTask();
//   }
  componentDidUpdate(prevProps, prevState) {
      if (prevState.searchString !== this.state.searchString) {

      }
  }

  onChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  onChangeSearchString(e) {
      this.setState({searchString: e.target.value})
  }

  onSubmit(e) {
      e.preventDefault();
      axios.post(endpoint + "/search", null, 
      {
          params: {q: this.state.searchString}
      }).then((response) => { 
          console.log(response);
          this.setState({searchResults: response.data});
        })
        // .catch(error => {console.log(error.response)})
    };

    displayList = () =>
        this.state.searchResults.map(result => <Button>{result}</Button>)

  render() {
    return (
      <div>
        <div className="row">
          <Header className="header" as="h2">
            Shakesearch
          </Header>
        </div>
        <div className="row">
          <Form onSubmit={this.onSubmit}>
              <Form.Field>
                <Input
                    name='searchInput'
                    value={this.state.searchString}
                    type="text"
                    onChange={this.onChangeSearchString}
                    placeholder="What art thee looking f'r"
                />
              </Form.Field>
          </Form>
        </div>
        <div className="row">
        <Button.Group basic vertical>
            {this.displayList()}
        </Button.Group>
        </div>
      </div>
    );
  }
}

export default ShakeSite;
