import React, { Component } from "react";
import axios from "axios";
import { Header, Form} from "semantic-ui-react";
import { Input, Button, ButtonGroup, Row, Container, Col, Card} from 'react-bootstrap';

let endpoint = "http://localhost:3001";

class ShakeSite extends Component {
  constructor(props) {
    super(props);
    // this.onSubmit = this.onSubmit.bind(this);
    // this.onChangeSearchString = this.onChangeSearchString.bind(this);

    this.state = {
    //   searchString: '',
      searchResults: [['hammie',1]],
      selectedLineContext: ''
    };

    this.searchString = React.createRef();
  }

onChangeSearchString = (e) => {
      this.setState({searchString: e.target.value})
  }

  onSubmit = (e) => {
      e.preventDefault();
      axios.post(endpoint + "/search", null, 
      {
          params: {q: this.searchString.current.value}
      }).then((response) => { 
          console.log(response);
          this.setState({searchResults: response.data});
        })
        // .catch(error => {console.log(error.response)})
    };

    displayList = () =>
        this.state.searchResults.map(result => 
                                                    <Button 
                                                        className='m-1' 
                                                        variant="light" 
                                                        size="sm" 
                                                        onClick={() => {this.searchResultOnClick(result[1])}}
                                                    >
                                                        {this.withNewlines(result[0])}
                                                    </Button>
                                                )

    searchResultOnClick = (lineNumber) => {
        axios.post(endpoint + "/getLineContext", null, 
        {
            params: {q: lineNumber}
        }).then((response) => { 
            console.log(response);
            this.setState({selectedLineContext: response.data});
          })
    }

    withNewlines = (text) => text.split('\n').map(i => {
        return <p>{i}</p>
    });

  render() {
    return (
        <Container fluid>
            <Row className="show-grid">
                <Col>
                    <Row className="m-1">
                        <Col>Shakesearch</Col>
                    </Row>
                    <Row className="m-1">
                        <Container fluid>
                            {/* <Form onSubmit={this.onSubmit}>
                                <Form.Field>
                                    <InputGroup
                                        name='searchInput'
                                        value={this.state.searchString}
                                        type="text"
                                        onChange={this.onChangeSearchString}
                                        placeholder="What art thee looking f'r"
                                    />
                                </Form.Field>
                            </Form> */}
                            <Form >
                                <Form.Group controlId="formBasicEmail">
                                    <Form.Label>Shakesearch!</Form.Label>
                                    <Form.Control 
                                    ref={this.searchString}
                                    type="text" 
                                    placeholder="What art thee looking f'r" 
                                    onSubmit={() => this.onSubmit()} 
                                    />
                                </Form.Group>
                            </Form>
                        </Container>
                    </Row>
                    {this.displayList()}

                </Col>

                <Col>
                    <Card fluid className="m-1">
                        <Card.Body>
                            {this.withNewlines(this.state.selectedLineContext)}
                        </Card.Body>    
                    </Card>
                </Col>
            </Row>

        </Container>
    );
  }
}

export default ShakeSite;
