//       this.setState({searchString: value});
//       e.preventDefault();
//       this.getSearch(this.searchString);
//   };
  onChangeSearchString(e) {
      this.setState({searchString: e.target.value})
  }

  onSubmit(e) {
      e.preventDefault();
      axios.post(endpoint + "/search", null, 
      {
          params: {q: this.state.searchString}
      }).then((response) => this.setState({searchResults: response.data}))
    };

    displayList = () =>
        this.state.searchResults.map(result => (<List.Item>{result}</List.Item>))

  render() {
    return (
      <div>
        <div className="row">
          <Header className="header" as="h2">
            Shakesearch
          </Header>
        </div>
        <div className="row">
          <Form onSubmit={this.onSubmit}>
              <Form.Field>
                <Input
                    name='searchInput'
                    value={this.state.searchString}
                    type="text"
                    onChange={this.onChangeSearchString}
                    placeholder="What art thee looking f'r"
                />
                <Input type="submit" value="Submit"/>
              </Form.Field>
          </Form>
        </div>
        <div className="row">
        <List>
            {this.displayList()}
          <List.Item>Apples</List.Item>
          <List.Item>Pears</List.Item>
          <List.Item>Oranges</List.Item>
        </List>
        </div>
      </div>
    );
  }
}

export default ShakeSite;
